<div class="container">
  <ng-container *ngFor="let summary of carouselData">
    <app-carousel-slide *ngIf="summary===currentSummary" class="slide" [slideData]="summary" @slideAnimation></app-carousel-slide>
  </ng-container>

      <button (click)="previousSlide()" class="control control-prev">
        <span class="arrow arrow-left"></span>
      </button>
      <button (click)="nextSlide()" class="control control-next">
        <span class="arrow arrow-right"></span>
      </button>

</div>
