import {Component, Input, OnInit} from '@angular/core';
import {ISummary} from "../interfaces/cv-interfaces";

@Component({
  selector: 'app-carousel',
  templateUrl: './carousel.component.html',
  styleUrls: ['./carousel.component.css']
})
export class CarouselComponent implements OnInit {
@Input()
  carouselData: Array<ISummary>

  public currentSummary: ISummary;

public ngOnInit(): void {
  this.currentSummary = this.carouselData[0];
}

public changeSummary(): void {
  const currentIndex: number = this.carouselData.indexOf(this.currentSummary);
  if(currentIndex >= this.carouselData.length) {
    this.currentSummary = this.carouselData[0]
  } else {
    this.currentSummary = this.carouselData[(currentIndex+1)]
  }

}
}


