import { Injectable } from '@angular/core';
import {Observable, Subject} from "rxjs";
import {IDetail, ISectionData, ISummary} from "../interfaces/cv-interfaces";





@Injectable({
  providedIn: 'root'
})
export class DataService {
  private dummyData: Array<ISectionData> = [{
    sectionName: 'blah',
    data: [
      {
        title: 'firstJob',
        organisation: 'firstOrg',
        startDate: '2021-01-01',
        endDate: '2022-02-02',
        detailList: [
          {
            description: 'asdadsdaasdadasd',
            tags: ['technical', 'organisation']
          },
          {
            description: 'basldjbasdkashdj',
            tags: ['technical']
          },
          {
            description: 'ccccccbsmdcshdbjshdbsg',
            tags: ['teamwork']
          }

        ]
      }
    ]
  }]
  private _cvSectionSubject: Subject<Array<ISectionData>> = new Subject<Array<ISectionData>>()

  public cvSections$: Observable<Array<ISectionData>>

  constructor() {
    this.cvSections$ = this._cvSectionSubject.asObservable();
    this._cvSectionSubject.next(this.dummyData)

  }

  public fetchData(): void {
    this._cvSectionSubject.next(this.dummyData)
  }

  public filterData(filters: Array<string>): void {
    const filteredResults: Array<ISectionData> = this.dummyData.map((section: ISectionData) => {
      return {
        sectionName: section.sectionName,
        data: section.data.map((summary: ISummary) => {
          return {
            ...summary,
            detailList: summary.detailList?.filter((detail: IDetail) => detail.tags.some((tag: string) => filters.includes(tag)))
          }
        })
      }
    })

    this._cvSectionSubject.next(filteredResults)


  }




}
