import {Component, OnInit} from '@angular/core';
import {DataService} from "../services/data.service";
import {Observable} from "rxjs";
import {IFilter, IProfile, ISectionData} from "../interfaces/cv-interfaces";
import {transition, trigger, useAnimation} from "@angular/animations";
import {fadeOut, fadeIn, scaleOut} from "../animations";
import AOS from "aos"

@Component({
  selector: 'app-cv-view',
  templateUrl: './cv-view.component.html',
  styleUrls: ['./cv-view.component.css'],
  animations: [
    trigger("fadeAnimation", [
      transition("void => *", [useAnimation(fadeIn, {params: { time: '500ms' }} )]),
      transition("* => void", [useAnimation(fadeOut, {params: { time: '500ms' }})]),
    ])
  ]
})
export class CvViewComponent implements OnInit {
  private _dataService: DataService;
  public sections$: Observable<Array<ISectionData>>;
  public profileData: IProfile;


  constructor(dataService: DataService) {
    this._dataService = dataService
  }

  public ngOnInit(): void {
    AOS.init();
    this.profileData = this._dataService.getProfile();
    this.sections$ = this._dataService.cvSections$;
  }

}
<div class="cv-wrapper">
  <div class="name-container">
    <h2 class="name">{{profileData.name}} </h2>
    <h4 class="jobTitle">{{profileData.jobTitle}}</h4>
  </div>
  <app-profile-view [profileData]="profileData"></app-profile-view>

  <div data-aos="fade-up" data-aos-duration="3000" *ngIf="sections$ | async as sections" class="cv-section-list">
    <app-section-view class="cv-section" *ngFor="let section of sections" [sectionData]="section" ></app-section-view>
  </div>

</div>
